var isPalindromic = function (str) {
    let len = str.length;
    for(let i=0; i<len; i++){
		if(str.charAt(i) !== str.charAt(len-1-i)){
			return false;
		}
    }
	return true;
};

/** 
 * 我认为最大的那个子字符串要么是中点对称的奇数串
 * 要么是镜像对象的偶数串。
 * 
*/
var longestPalindrome = function (str) {
    const begin = new Date();

    if (str.length == 0) {
        return '';
    }
    if (str.length == 1) {
        return str;
    }

    //全是同一个字母
    // if (isPalindromic(str)) {
    //     return str;
    // }

    //有很多相同字母


    let evenMax = 0;
    let evenResult = str.charAt(0);

    //如果最大的是一个偶数，则依次从中心点
    for (let i = 1; i <= str.length - 1; i++) {
        //在这里开始，把tempMax改为evenMax，没evenMax大的，也没算的意义。
        let tempMax = evenMax;
        let tempResult = evenResult;
        let left = i - evenMax / 2 - 1 ;
        let right = i + evenMax / 2 + 1;
        if (left < 0 || right > str.length) {
            continue;
        }
        while (isPalindromic(str.substring(left, right))) {
            tempMax += 2;
            tempResult = str.substring(left, right);
            left--;
            right++;
            if (left < 0 || right > str.length) {
                break;
            }
        }
        if (tempMax > evenMax) {
            evenMax = tempMax;
            evenResult = tempResult;
        }
    }

    //如果最大的是一个奇数，则依次从中心点
    //直接设置为evenMax,比evenMax小的无比较的意义
    let oddMax = 1;
    let oddResult = str.charAt(0);
    for (let i = 1; i < str.length; i++) {
        let tempMax = oddMax;
        let tempResult = oddResult;
        let left = i - evenMax / 2;
        let right = i + evenMax / 2;
        if (right <= str.length - 1 && left >= 0) {
            while (isPalindromic(str.substring(left, right+1))) {
                tempMax = right + 1 - left;
                tempResult = str.substring(left, right + 1);
                left--;
                right++;
                if (left < 0 || right >= str.length) {
                    break;
                }
            }
        }
        
        if (tempMax > oddMax) {
            oddMax = tempMax;
            oddResult = tempResult;
        }

    }


    const end = new Date();
    console.log(end - begin);
    if (oddResult.length > evenResult.length) {
        return oddResult;
    }
    else {
        return evenResult;
    }
    //return oddMax > evenMax ? oddResult : evenResult;
};

console.log(longestPalindrome("aaaaa"));//aaaaa
console.log(longestPalindrome("aaaa"));//aaaa
console.log(longestPalindrome("baab"));//baab
console.log(longestPalindrome("djka akjd"));//djka akjd
console.log(longestPalindrome("a"));//a
console.log(longestPalindrome("babad"));//bab
console.log(longestPalindrome("cbbd"));//bb
console.log(longestPalindrome("bb"));//bb

